import React, { useState } from "react";
import "./ExpenseForm.css";
const ExpenseForm = () => {
  const [changeTitle, setChangeTitle] = useState(''); // by default input field will be blank
  const [changeAmount, setChangeAmount] = useState('');
  const [changeDate, setChangeDate] = useState('');

  const titleChangeHandler = (event) => {
    setChangeTitle(event.target.value)
    console.log(event.target.value)

  };
  const amountChangeHandler = (event) => {
    setChangeAmount(event.target.value)

  };
  const dateChangeHandler = (event) => {
    setChangeDate(event.target.value)

  };
  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangeHandler} /> 
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" min="0.01" step="0.01" onChange={amountChangeHandler}/>
        </div>
        <div className="new-expense__control">
          <label>DATE</label>
          <input type="date" min="2009-01-02" max="2022-12-31" onChange={dateChangeHandler}/>
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
